sources.scss/main.scss:
@import url('https://fonts.googleapis.com/css2?family=Open+Sans:wght@300;400;600&display=swap');

// Color Declarations
$header: #f9fafa;
$white: #fff;
$text-color: #28b1ec;
$text: #444;
$text2: #ff951f;
$text3: #0f52ba;
$text4: #dd4b39;
$background-color: #404f58;
$orange: #f79f1f;

* {
  font-family: 'Open Sans', sans-serif;
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  outline: none;
  border: none;
  text-decoration: none;
  transition: all 0.2s linear;
}

html {
  font-size: 62.5%;
  overflow-x: hidden;
  scroll-padding-top: 7rem;
  scroll-behavior: smooth;
}

.heading {
  text-align: center;
  font-size: 3.5rem;
  padding-bottom: 2rem;
  margin-bottom: 2rem;
  color: $text;
}

section {
  padding: 5.8% 9%;
}

// header

.header {
  position: relative;
  display: flex;
  align-items: center;
  justify-content: space-between;
  background: $header;
  padding: 2rem 9%;

  .logo {
    color: $text3;
    font-size: 2.3rem;
    text-transform: uppercase;
  }
  .navbar {
    a {
      margin: 0 1rem;
      font-size: 1.9rem;
      color: $text3;
    }
  }
  .btn {
    display: inline-block;
    padding: 0.9rem 1.5rem;
    color: $text3;
    background: none;
    border: 1px solid $text3;
    cursor: pointer;
    font-size: 1.7rem;

    i {
      margin-left: 0.3rem;
    }
    &:hover {
      -webkit-border-radius: 5px;
      -moz-border-radius: 5px;
      border-radius: 5px;
    }
  }
  #menu-btn {
    font-size: 3rem;
    color: $text3;
    cursor: pointer;
    display: none;
  }
}

// Home

.home {
  background: $header;
  padding-top: 6rem;
  display: flex;
  flex-wrap: wrap;
  gap: 1.5rem;
  align-items: center;

  .content {
    flex: 1 1 40rem;

    h3 {
      font-size: 3.8rem;
      color: $text;
      font-weight: 700;
    }
    h4 {
      font-size: 2.8rem;
      color: $text;
      font-weight: 300;
      margin-bottom: 0.5rem;
    }
    p {
      font-size: 1.7rem;
      color: $text;
      padding: 1rem 0;
      max-width: 85%;
      line-height: 1.6;
    }
    .btn {
      display: inline-block;
      padding: 0.9rem 1.5rem;
      color: $text3;
      background: none;
      border: 1px solid $text3;
      cursor: pointer;
      font-size: 1.7rem;

      &:hover {
        -webkit-border-radius: 5px;
        -moz-border-radius: 5px;
        border-radius: 5px;
      }
    }
    .button {
      display: flex;
      gap: 1.7rem;
      align-items: center;
      margin: 2rem 0;

      h6 {
        font-size: 2.1rem;
        color: $text-color;
      }
    }
  }

  .image {
    flex: 1 1 40rem;

    img {
      width: 100%;
    }
  }
}

// Features

.features {
  .box-container {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(26rem, 1fr));
    gap: 1.5rem;
  }
  .box {
    padding: 3rem 2rem;

    i {
      font-size: 5rem;
      color: $text-color;
      margin-bottom: 2.5rem;
    }
    h4 {
      font-size: 2.1rem;
      margin-bottom: 1.5rem;
      font-weight: 400;
      color: $text;
    }
    p {
      color: $text;
      font-size: 1.7rem;
      font-weight: 300;
      line-height: 1.7;
    }
  }
}

// Pricing

.pricing {
  background: $background-color;
  .box-container {
    display: flex;
    align-items: center;
    justify-content: center;
    flex-wrap: wrap;

    .box {
      flex: 0.2 0.2 27rem;
      margin: 1rem;
      padding: 1rem;
      background: #fff;
      border: 0.1rem solid rgba(0, 0, 0, 0.2);
      box-shadow: 0 0.5rem 1rem rgba(0, 0, 0, 0.1);
      border-radius: 0.5rem;
      text-align: center;
      padding-bottom: 3rem;

      .btn {
        display: inline-block;
        cursor: pointer;
        margin-top: 1rem;
        padding: 0.8rem 3rem;
        border-radius: 5rem;
        background: conic-gradient(
          from 90deg at bottom right,
          cyan,
          rebeccapurple
        );
        font-size: 1.7rem;
        color: #fff;
        cursor: pointer;
        box-shadow: 0 0.5rem 1rem rgba(0, 0, 0, 0.1);
        transition: all 0.3s linear;

        &:hover {
          opacity: 0.9;
        }
      }

      .title {
        color: $text;
        font-size: 2.5rem;
        padding-top: 1rem;
        text-transform: capitalize;
      }
      .price {
        font-size: 2.6rem;
        color: $text-color;
        padding: 1rem 0;
        span {
          font-size: 2rem;
        }
      }
      ul {
        padding: 1rem 0;
        list-style: none;

        li {
          font-size: 1.7rem;
          color: $text;
          padding: 0.5rem 0;

          .fa-check {
            color: lightgreen;
          }
          .fa-times {
            color: tomato;
          }
        }
      }
    }
  }
}

.pricing .box-container .box:nth-child(2),
.pricing .box-container .box:hover {
  border: 0.4rem solid $text3;
}

// Reviews

.review {
  .review-slider {
    background: $white;
    padding: 1.5rem;
    cursor: pointer;
    color: $white;
    border-radius: 0.5rem;
    box-shadow: 0 0.5rem 1.5rem rgba(0, 0, 0, 0.1);

    .slide {
      background: $white;
      text-align: center;
      padding: 3rem;

      img {
        height: 10rem;
        width: 10rem;
        border-radius: 50%;
        object-fit: cover;
        margin-bottom: 0.5rem;
        box-shadow: 0 0.5rem 1.5rem rgba(0, 0, 0, 0.1);
      }
      h3 {
        font-size: 2.5rem;
        text-transform: capitalize;
        color: $text;
      }
      p {
        font-size: 1.6rem;
        max-width: 80%;
        margin: auto;
        color: $text;
        line-height: 1.7;
        padding: 1rem 0;

        span {
          font-size: 3.5rem;
          color: $text-color;
        }
      }
      .stars {
        padding: 1rem 0;
        padding-bottom: 1rem;

        i {
          font-size: 2rem;
          color: $orange;
        }
      }
    }
  }
  .swiper-pagination {
    position: static;
    text-align: center;
    transition: 0.3s opacity;
    transform: translate3d(0, 0, 0);
    z-index: 10;
  }
}

.swiper-pagination-bullet-active {
  background: $text;
}

// Support
.support {
  background: $header;

  .box-container {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(26rem, 1fr));
    gap: 1rem;

    ul {
      padding: 0 3rem 2rem;
      li {
        list-style: none;
        .question {
          font-size: 2.2rem;
          margin: 1.5rem 0;
          color: $text3;
          font-weight: 600;
        }
        .answer {
          font-size: 1.8rem;
          font-weight: 300;
          line-height: 1.5;
          color: $text;
        }
      }
    }
  }
  .support-forum {
    text-align: center;
    margin-top: 2.3rem;
    h2 {
      font-size: 3.5rem;
      margin-bottom: 1.3rem;
      color: $text3;
    }
    a {
      font-size: 2.7rem;
      color: $text-color;

      &:hover {
        border-bottom: 1.3px solid $text-color;
      }
    }
  }
}

.newsletter {
  text-align: center;
  padding: 5rem 1rem;
  background: $background-color;
  .box-container {
    width: 50%;
    margin: auto;
  }
  h3 {
    color: #fff;
    font-size: 3rem;
    text-transform: uppercase;
  }
  p {
    color: #fff;
    font-size: 1.6rem;
    margin: 2.3rem auto;
    text-align: left;
    line-height: 1.7;
  }
  form {
    display: flex;
    max-width: 70rem;
    border: 0.2rem solid #fff;
    padding: 0.5rem;
    border-radius: 5rem;
    margin: 2rem auto;
    height: 5.5rem;
    input[type='email'] {
      padding: 0 2rem;
      font-size: 1.7rem;
      background: none;
      width: 100%;
      color: #fff;
      text-transform: none;
      background: none;
      &::placeholder {
        color: #fff;
        text-transform: capitalize;
      }
    }
    input[type='submit'] {
      background: #fff;
      width: 20rem;
      font-size: 1.7rem;
      border-radius: 5rem;
      cursor: pointer;
      &:hover {
        opacity: 0.8;
      }
    }
  }
}

// Footer

footer {
  padding: 3rem 2% 0;

  .box-container {
    display: flex;
    flex-wrap: wrap;
    .box {
      flex: 1 1 25rem;
      margin: 2rem;
      h3 {
        font-size: 2.5rem;
        padding: 1rem 0;
        color: $text;
        text-decoration: underline;
        text-underline-offset: 1rem;
        text-transform: capitalize;
      }
      p {
        font-size: 1.8rem;
        color: $text3;
      }

      a {
        display: block;
        font-size: 1.8rem;
        padding: 0.5rem 0;
        color: $text3;
        &:hover {
          text-decoration: underline;
        }
      }
      .info {
        display: flex;
        align-items: center;
        i {
          margin: 0.5rem 0;
          margin-right: 1rem;
          border-radius: 50%;
          background: $text3;
          color: $white;
          font-size: 1.5rem;
          height: 3.5rem;
          width: 3.5rem;
          line-height: 3.5rem;
          text-align: center;
        }
      }
      .fab {
        color: $text3;
        font-size: 3rem;
      }
    }
  }
  .credit {
    font-size: 2rem;
    font-weight: normal;
    letter-spacing: 0.1rem;
    color: $text;
    border-top: 0.1rem solid #fff5;
    padding: 2.5rem 1rem;
    text-align: center;
  }
}

// Media Queries

@media (max-width: 991px) {
  html {
    font-size: 55%;
  }
  .header {
    padding: 2rem;
  }
  section {
    padding: 4rem 9%;
  }
}

@media (max-width: 800px) {
  .support {
    .box-container {
      display: block;
      ul {
        padding: 0.8rem 2rem;
      }
    }
  }
  .newsletter {
    .box-container {
      width: 75.5%;
    }
  }
}

@media (max-width: 768px) {
  section {
    padding: 7% 9%;
  }
  .header {
    #menu-btn {
      display: initial;
    }
    .navbar {
      position: absolute;
      top: 99%;
      left: 0;
      right: 0;
      background: $header;
      clip-path: polygon(0 0, 100% 0, 100% 0, 0 0);

      &.active {
        clip-path: polygon(0 0, 100% 0, 100% 100%, 0% 100%);
      }

      a {
        display: block;
        font-size: 2.2rem;
        margin: 0;
        padding: 1.5rem 2rem;
      }
    }
  }
}

@media (max-width: 450px) {
  html {
    font-size: 50%;
  }

  .heading {
    margin-bottom: 3.2rem;
  }
  .features {
    .box-container {
      .box {
        padding-top: 0.5rem;

        p {
          font-size: 2rem;
        }
      }
    }
  }
  .pricing {
    .box-container {
      .box {
        min-width: 90%;
      }
    }
  }
  .newsletter {
    .box-container {
      width: 90%;
    }
  }
  section {
    padding: 6rem 9%;
  }
}
@media (max-width: 400px) {
  .home {
    .content {
      p {
        max-width: 100%;
      }
    }
  }
}
