  margin: 2rem;
}
footer .box-container .box h3 {
  font-size: 2.5rem;
  padding: 1rem 0;
  color: #444;
  text-decoration: underline;
  text-underline-offset: 1rem;
  text-transform: capitalize;
}
footer .box-container .box p {
  font-size: 1.8rem;
  color: #0f52ba;
}
footer .box-container .box a {
  display: block;
  font-size: 1.8rem;
  padding: 0.5rem 0;
  color: #0f52ba;
}
footer .box-container .box a:hover {
  text-decoration: underline;
}
footer .box-container .box .info {
  display: flex;
  align-items: center;
}
footer .box-container .box .info i {
  margin: 0.5rem 0;
  margin-right: 1rem;
  border-radius: 50%;
  background: #0f52ba;
  color: #fff;
  font-size: 1.5rem;
  height: 3.5rem;
  width: 3.5rem;
  line-height: 3.5rem;
  text-align: center;
}
footer .box-container .box .fab {
  color: #0f52ba;
  font-size: 3rem;
}
footer .credit {
  font-size: 2rem;
  font-weight: normal;
  letter-spacing: 0.1rem;
  color: #444;
  border-top: 0.1rem solid #fff5;
  padding: 2.5rem 1rem;
  text-align: center;
}

@media (max-width: 991px) {
  html {
    font-size: 55%;
  }

  .header {
    padding: 2rem;
  }

  section {
    padding: 4rem 9%;
  }
}
@media (max-width: 800px) {
  .support .box-container {
    display: block;
  }
  .support .box-container ul {
    padding: 0.8rem 2rem;
  }

  .newsletter .box-container {
    width: 75.5%;
  }
}
@media (max-width: 768px) {
  section {
    padding: 7% 9%;
  }

  .header #menu-btn {
    display: initial;
  }
  .header .navbar {
    position: absolute;
    top: 99%;
    left: 0;
    right: 0;
    background: #f9fafa;
    clip-path: polygon(0 0, 100% 0, 100% 0, 0 0);
  }
  .header .navbar.active {
    clip-path: polygon(0 0, 100% 0, 100% 100%, 0% 100%);
  }
  .header .navbar a {
    display: block;
    font-size: 2.2rem;
    margin: 0;
    padding: 1.5rem 2rem;
  }
}
@media (max-width: 450px) {
  html {
    font-size: 50%;
  }

  .heading {
    margin-bottom: 3.2rem;
  }

  .features .box-container .box {
    padding-top: 0.5rem;
  }
  .features .box-container .box p {
    font-size: 2rem;
  }

  .pricing .box-container .box {
    min-width: 90%;
  }

  .newsletter .box-container {
    width: 90%;
  }

  section {
    padding: 6rem 9%;
  }
}
@media (max-width: 400px) {
  .home .content p {
    max-width: 100%;
  }
}

